import {useEffect, useState, Fragment} from 'react';
import axios from 'axios';
import Authorize from './authorize';
import Main from './mainpage';

const Playlist = ({props}) => {
  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])

  async function makePL(Name, Description){
    let idgetter = axios.get('https://api.spotify.com/v1/me', {
        headers: {
            'Authorization' : `Bearer ${token}`
        }
    })
    console.log(idgetter)
    let ID = idgetter.id
    console.log('https://api.spotify.com/v1/users/' + ID + '/playlists')
    let response = axios('https://api.spotify.com/v1/users/' + ID + '/playlists', {
        method: 'POST',
        headers: {
            'Authorization' : `Bearer ${token}`,
            'Content-Type': 'application/json'
        },
        data: JSON.stringify({
            name: Name,
            description: Description
        })
    })
    console.log(response)
  }

  return(

      <div className='grid justify-center mt-16 space-y-8'>
        <h1 className='sm:text-3xl text-xl font-bold text-white text-center'> Step 3: Save your recommendations</h1>
            {token ?
              <button onClick={() => {makePL("test", "spotify api test")}}
              className='rounded-3xl bg-[#1DB954] py-2 px-2 font-bold text-xl scale-75 sm:scale-100
              hover:scale-105 hover:bg-[#2bde6a]'> Create a playlist.
              </button>
                : <h2 className='text-center text-white'> Please login to your Spotify account first. </h2>
              }
        </div>

  )
}

export default Playlist;
