import {useEffect, useState, createContext, useContext} from 'react';
import axios from 'axios';
import Authorize from './authorize';
import TopSongs from './TopSongs';
import Recs from './Recommendations';
import FadeIn from 'react-fade-in/lib/FadeIn';
import { FaArrowUp } from "react-icons/fa";
import Playlist from './CreatePlaylist';

const Main = () => {
  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])


const [datatopass, setDataToPass] = useState([])
const [displayrec, setDisplayrec] = useState(false)
const [recstopass, setRecstopass] = useState([])
const [displaypl, setDisplaypl] = useState(false)

  function CallBack (childData) {
    setDataToPass(Array.from(childData))
    setDisplayrec(true)
  }
  function CallBackRec (childData) {
    setRecstopass(Array.from(childData))
    setDisplaypl(true)
  }

  return(
    <div id="main" className='max-w-[800px] mt-[-96px] w-full h-screen mx-auto text-center'>
      <TopSongs handleCallback={CallBack}/>
      {displayrec ? <FadeIn delay="200" transitionDuration="2000"> <Recs props={datatopass} handleCallback={CallBackRec}/> </FadeIn> 
      : <FadeIn delay="1000" transitionDuration="800"> <FaArrowUp size={40} className='fill-white scale-75 sm:scale-100 lg:scale-150 mt-16 mb-4 mx-auto'/>
      <text className='font-bold text-white text-center'> Click Here. </text> 
      </FadeIn>
      }
      {displaypl ? <FadeIn delay="200" transitionDuration="2000"> <Playlist recs={recstopass}/> </FadeIn> 
      : <FadeIn delay="1000" transitionDuration="800"> <FaArrowUp size={40} className='fill-white scale-75 sm:scale-100 lg:scale-150 mt-16 mb-4 mx-auto'/>
      <text className='font-bold text-white text-center'> Click Here. </text> 
      </FadeIn>
      }

    </div>
  )
}

export default Main;
