import {useEffect, useState} from 'react';
import axios from 'axios';
import Authorize from './authorize';
import Main from './mainpage';

const Recs = ({props}) => {
  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])

  const [reco, setReco] = useState([])

  async function getRecs(nsongs, trackids){
    console.log(props.message)
    console.log(`https://api.spotify.com/v1/recommendations?limit=${nsongs}?seed_tracks=${trackids.join(',')}`)
    const {data} = await axios.get(`https://api.spotify.com/v1/recommendations?limit=${nsongs}?seed_tracks=${trackids.join(',')}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }) 
    setReco(data.items)
  }

  const renderRecs = () => {
    return reco.map(tracks => (
        <div key={tracks.id}>
            {tracks.images ? <img width={"100%"} src={tracks.images[0].url} alt=""/> : <span></span>}
            {tracks.name} by {tracks.artists[0].name}
        </div>
    ))
  }

  return(

      <div className='grid justify-center my-16 space-y-16'>
        <h1 className='text-3xl font-bold'> Step 2: Get your recommendations </h1>
            {token ?
              <button onClick={() => {getRecs(5, efwef)}}
              className='rounded-md bg-[#1DB954] py-2 px-2 font-bold text-xl'> Get Recommendations
              </button>
                : <h2 className='text-black'> Please login to your Spotify account first. </h2>
              }
              {renderRecs()}
        </div>

  )
}

export default Recs;



