import logo from './logo.svg';
import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';

function App() {
  const CLIENT_ID = "445063991dde42efbaa3536012567e7d"
  const REDIRECT_URI = "http://localhost:3000"
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
  const SCOPE = "user-top-read"
  const RESPONSE_TYPE = "token"

  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])
  //logs out by removing token
  const logout = () => {
    setToken("")
    window.localStorage.removeItem("token")
}

  const [tops, setTops] = useState([])

  async function topTracks(timerange, nsongs){
    const {data} = await axios.get(`https://api.spotify.com/v1/me/top/tracks?time_range=${timerange}&limit=${nsongs}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    list_of_results = data[0]["items"]
    list_of_artist_names = []
    list_of_artist_uri = []
    list_of_song_names = []
    list_of_song_uri = []
    list_of_durations_ms = []
    list_of_explicit = []
    list_of_albums = []
    list_of_popularity = []
    list_of_track_ids = []
    for (result in list_of_results) {
      result["album"]
      this_artists_name = result["artists"][0]["name"]
      list_of_artist_names.append(this_artists_name)
      this_artists_uri = result["artists"][0]["uri"]
      list_of_artist_uri.append(this_artists_uri)
      list_of_songs = result["name"]
      list_of_song_names.append(list_of_songs)
      song_uri = result["uri"]
      list_of_song_uri.append(song_uri)
      list_of_duration = result["duration_ms"]
      list_of_durations_ms.append(list_of_duration)
      song_explicit = result["explicit"]
      list_of_explicit.append(song_explicit)
      this_album = result["album"]["name"]
      list_of_albums.append(this_album)
      song_popularity = result["popularity"]
      list_of_popularity.append(song_popularity)
      song_id = result["id"]
      list_of_track_ids.append(song_id)
    }
    console.log(data.items)
    setTops(data)
  }



  return (
    <div className="App">
       <header className="App-header">
              <h1>Spotify React</h1>
{/* checking if an account is linked */}
                {!token ? <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&scope=${SCOPE}&response_type=${RESPONSE_TYPE}`}>Login to Spotify</a>
                    : <button onClick={logout}>Logout</button>}

              {token ?
              <button onClick={() => {topTracks("short_term", 30)}}> get top songs</button>

                : <h2> pls login </h2>
              }

      </header>

    </div>
  );
}

export default App;

