import {useEffect, useState, createContext, useContext, Fragment} from 'react';
import axios from 'axios';
import Authorize from './authorize';


const TopSongs = (props) => {
  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])

  const [tops, setTops] = useState([])
  const [display, setDisplay] = useState(true)

  async function topTracks(timerange, nsongs){
    const {data} = await axios.get(`https://api.spotify.com/v1/me/top/tracks?time_range=${timerange}&limit=${nsongs}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }) 
    const top_songs = data.items.map(({name, artists}) => `${name} by ${artists.map(artist=>artist.name).join(', ')}`)
    let top_songs_id = data.items.map(({id})=> `${id}`)
    props.handleCallback(top_songs_id)
    setTops(data.items)
  }

  const renderTops = () => {
    if (display)
    return (
        tops.map((items) => (
          <Fragment  key={items.id} >
          <button onClick={setDisplay}> hehehe </button>
          <span className=' flex justify-center space-x-8 mx-16'>
          <img src = {items.album.images[2].url} className='rounded-lg mx-8 scale-150 h-2/3 align-middle'/>
          <span className='space-y-2'>
          <p className='font-bold text-white truncate w-80'>{items.name + ' '} by {items.artists[0].name} </p>
          <audio controls src = {items.preview_url} className='color-[#1DB954]'> </audio>
          </span>
          {/* add play function*/}
          </span>
          </Fragment>
    )
    ))
  }

  return(
      <div className='my-4 space-y-12'>
        <span className='grid justify-center space-y-8'> 
        <h1 className='text-3xl font-bold text-white'> Step 1: Get your top songs </h1>
            {token ?
              <button onClick={() => {topTracks("short_term", 5)}}
              className='rounded-3xl bg-[#1DB954] py-2 px-2 font-bold text-xl hover:scale-105 hover:bg-[#2bde6a]'> Get Top Songs
              </button>
                : <h2 className='text-center text-white'> Please login to your Spotify account first. </h2>
              }
        </span>
              {renderTops()}
        </div>
  )
}

export default TopSongs;



