import logo from './logo.svg';
import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';
import Authorize from './authorize';


const TopSongs = () => {
  const [token, setToken] = useState("")

// gets token from local storage by splitting hash at accesstoken value
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

  }, [])

  const [tops, setTops] = useState([])

  async function topTracks(timerange, nsongs){
    const {data} = await axios.get(`https://api.spotify.com/v1/me/top/tracks?time_range=${timerange}&limit=${nsongs}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }) 
    const top_songs = data.items.map(({name, artists}) => `${name} by ${artists.map(artist=>artist.name).join(', ')}`)
    const top_songs_id = data.items.map(({id})=> `${id}`)
    console.log(top_songs_id)
    setTops()
  }

  return(
      <div className='flex-col justify-center my-4 space-y-16 space-x-16'>
        <h1 className='text-3xl font-bold'> Step 1: Get your top songs </h1>
            {token ?
              <button onClick={() => {topTracks("short_term", 30)}}
              className='rounded-md bg-[#1DB954] py-2 px-2 font-bold text-xl'> Get Top Songs
              </button>

                : <h2 className='text-white'> Please login to your Spotify account first. </h2>
              }
            </div>
  )
}

export default TopSongs;



